$(document).ready(function(){$("header").css({opacity:1});$(window).scroll(function(){var e=$(window).scrollTop(),t=$("header").outerHeight(),n=10,r=e*-1/t*2+1;$(".header-content").css({top:$(window).scrollTop()*.6,opacity:r});if(e>t-n){$("header").css({position:"fixed",top:t*-1+n});$("#content").css({marginTop:t})}else if(e<=t-n){$("header").css({position:"relative",top:""});$("#content").css({marginTop:0})}})});